import collectionsimport heapqif __name__ == '__main__':    s='bhppaaacvhjohcfaas'    count=collections.Counter(s)    heap=max(count.items(),key=lambda x:x[1])[1]    queue=[(-x[1],x[0]) for x in count.items()]    heapq.heapify(queue)    res=list()    while len(queue)>1:        _,l1=heapq.heappop(queue)        _,l2=heapq.heappop(queue)        res.extend([l1,l2])        count[l1]-=1        count[l2]-=1        if count[l1] > 0:            heapq.heappush(queue, (-count[l1],l1))        if count[l2] > 0:            heapq.heappush(queue, (-count[l2],l2))